import React from 'react'
import ReactDOM from 'react-dom'
import './styles.css'
import border from '../img/border.png'
import products from '../productslist'

import { useState, useEffect } from 'react'

import { useContext } from 'react';
import { bag, cart, cartNum } from '../bagcontext'

function ShoppingCartPage() {

    let selectedProduct = useContext(bag)
    let cartItems = useContext(cart) 
    let cartItemsNum = useContext(cartNum) 


     function removeItem(e) {
    //     for (let i = 0; i < products.length; i++) {
    //         if (e.target.name === products[i].name) {
    //             //cart = cart.filter(function(e) { return e !== products[i].name })
    //         }
    //     }
     }


 function MakeItems() {
    console.log('initial')
     if (cartItems !== []) {
        console.log('passed parameter')
         cartItems.map((indiv) =>  {
            console.log(indiv)
             return <>
                 <li className='item' key={indiv.id}>
                     <img className='item-img' src={indiv.image[0]} alt={indiv.alt} />
                     <div className='item-des'>
                         <div className='title'>{indiv.name}</div>
                         <div className='snippet'>{indiv.description}</div>
                     </div>
                     <div className='important'>
                         <div className='quantity'>{cartItems.length}</div>
                         <div className='price'>{indiv.price}</div>
                         <div className='adding'>Adding to:</div>
                         <div className='math'>{(indiv.price) / (cartItems.length)}</div>
                     </div>
                     <button className='remove' name={indiv.name} onClick={removeItem}>X</button>
                 </li>
             </>
         })
     }
 }


    return  <>
                <img id="border" src={border} alt='border image' />
                <div id="main">
                    <ul id="item-list">
                        <MakeItems />
                        <div id="bottom">
                            <button id="checkout">Checkout</button>
                        </div>
                    </ul>
                </div>
            </>
}

export default ShoppingCartPage


