import React from 'react'
import ReactDOM from 'react-dom'
import './styles.css'
import { CreateProducts } from '../products'
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'
import products from '../productslist'

function ProductInfo(props) {
    let selectedProduct = JSON.parse(sessionStorage.getItem("selectedProduct"))

    const [bagNumber, setBagNumber] = useState('')
    const [bagItems, setBagItem] = useState([])

    function setBagItems(e, {setCart}) {
        for(let i = 0; i < products.length; i++) {
            if (e.target.name === products[i].name) {
                bagItems.push(products[i])
                setBagItem(bagItems.push(products[i]))
                setCart(cart => [...cart, bagItems])
            }
        }
    }

    const handleChange = (event, {setCartNum}) => {
        setBagNumber(event.target.value)
        setCartNum(bagNumber)
      }

    const navigate = useNavigate();

    function goToBag() {
        navigate('/shopping-cart');
    }

    return  <>
                <img id="bag-popup" src="./img/shoppingcart.svg" onClick={goToBag}/>
                <div id="imgdes">
                    <div id="lefty">
                        <img src={'./img/' + (selectedProduct)[0].image[0]} alt={(selectedProduct)[0].alt} />
                    </div>
                    <div id="righty">
                        <div id='title'>{(selectedProduct)[0].name}</div>
                        <div id='itemtype'>{(selectedProduct)[0].snippet}</div>
                        <div id='price'>{(selectedProduct)[0].price}</div>
                        <div id='purchase'>
                            <input id='quantity' type="number" name="quantity" min="1" max="99" onChange={handleChange}></input>
                            <button id='bag' name={(selectedProduct)[0].name} onClick={setBagItems}>Add to Cart</button>
                        </div>
                        <div id='item-des'>{(selectedProduct)[0].description}</div>
                    </div>
                </div>
                <div id="body-cont">
                    <div id="reccs">Recommended for you:</div>
                    <ul id="products">
                        <CreateProducts />
                    </ul>
                </div>
            </>
}

export default ProductInfo
