import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import './styles.css'

import products from '../productslist'
import { CreateProducts } from '../products'
import ReSort from '../sorter'

function Homepage() {

    function ReSort() {

        const [pricesArray, setPricesArray] = useState([])
        const [sortedArray, setSortedArray] = useState([])

        let productAmount = document.getElementsByClassName("product")
        let list = document.getElementById("products")

        const getInitialState = () => {
            const value = "popular";
            return value;
          };

          const [value, setValue] = useState(getInitialState);

          const handleChange = (e) => {
              setValue(e.target.value);
          };

        if (value === "lowtohigh" && list.childElementCount > 1) {

                productAmount.map((productItem) => (
                    pricesArray.push((productItem.childNodes[2].textContent).replace('$', ''))
                ))

                setPricesArray(pricesArray.sort(function(a, b){return a-b}))

                productAmount.map((productItem) => (
                    products.map(function(productItemItem) {
                        if (pricesArray[productItem.id] == productItemItem.price && !sortedArray.includes(productItemItem)) {
                            setSortedArray(sortedArray.push(productItemItem))
                        }  
                    })
                ))

            return <>
            <Remover />
            {sortedArray.map((arrayItem) => (
                <li className='product' key={arrayItem.id}>
                    <img src={arrayItem.image[0]} alt={arrayItem.alt}/>
                    <div className='name'>{arrayItem.name}</div>
                    <div className='price'>{'$' + arrayItem.price}</div>
                </li>
            ))}    
            </>
        } else if (value === "hightolow" && list.childElementCount > 1) {

        }

    }


    return  <>
                <div id="top">
                    <img id="title-img" />
                    <div id="main-title" className="title"></div>
                </div>
                <div id="main">
                    <div id="topics">
                        <form id="input">
                            <div>
                                <input className="checkbox" id="Instock" type="checkbox" name="Instock" value="Instock" />
                                <label className="check">In-stock</label>
                            </div>
                            <input id="parameter-btn" type="button" value="Submit" />
                        </form>
                    </div>
                    <div id="body-cont">
                        <div id="sorting"> 
                            <select name="inputdata" id="sort" value={value} onChange={handleChange} >
                                <option value="popular">Reccomended</option>
                                <option value="lowtohigh">Price: low to high</option>
                                <option value="hightolow">Price: high to low</option>
                            </select>
                        </div>
                        <ul id="products">
                            <CreateProducts />
                        </ul>
                    </div>
                </div>
            </>
}

export default Homepage
