import React from 'react'
import ReactDOM from 'react-dom'
import './styles.css'
import border from '../img/border.png'
import products from '../productslist'

import { useState, useEffect } from 'react'

import { useContext } from 'react';
import { bag, cart, cartNum } from '../bagcontext'

function ShoppingCartPage() {

    let selectedProduct = useContext(bag)
    let cartItems = useContext(cart) 
    let cartItemsNum = useContext(cartNum) 


    function removeItem(e, {cart, setFinalCart}) {
        for (let i = 0; i < products.length; i++) {
            if (e.target.name === products[i].name) {
                cart = cart.filter(function(e) { return e !== products[i].name })
                //setFinalCart(cart)
            }
        }
        window.location.reload()
    }


