import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import products from './productslist'
import Remover from './remover'

function ReSort(thisvalue) {

    const [pricesArray, setPricesArray] = useState([])
    const [sortedArray, setSortedArray] = useState([])

    let productAmount = document.getElementsByClassName("product")
    let list = document.getElementById("products")

    if (thisvalue === "lowtohigh" && list.childElementCount > 1) {

            productAmount.map((productItem) => (
                pricesArray.push((productItem.childNodes[2].textContent).replace('$', ''))
            ))

            setPricesArray(pricesArray.sort(function(a, b){return a-b}))

            productAmount.map((productItem) => (
                products.map(function(productItemItem) {
                    if (pricesArray[productItem.id] == productItemItem.price && !sortedArray.includes(productItemItem)) {
                        setSortedArray(sortedArray.push(productItemItem))
                    }  
                })
            ))

        return <>
        <Remover />
        {sortedArray.map((arrayItem) => (
            <li className='product' key={arrayItem.id}>
                <img src={arrayItem.image[0]} alt={arrayItem.alt}/>
                <div className='name'>{arrayItem.name}</div>
                <div className='price'>{'$' + arrayItem.price}</div>
            </li>
        ))}    
        </>
    } else if (thisvalue === "hightolow" && list.childElementCount > 1) {

    }




}

export default ReSort

