import React from 'react'
import ReactDOM from 'react-dom'
import './styles.css'
import border from '../img/border.png'
import products from '../productslist'

import { useContext, useState, useEffect } from 'react';
import { cart, cartNum } from '../bagcontext'
import { useNavigate } from 'react-router-dom';

function ShoppingCartPage() {

    let cartItems = useContext(cart) 
    let cartItemsNum = useContext(cartNum) 

    const navigate = useNavigate()

    const [listItems, setListItems] = useState([])
    const [absoluteTotal, setAbsoluteTotal] = useState(0)

    function goToCheckout() {
        navigate('/checkout')
    }

        setListItems(cartItems)

     function removeItem(e) {
         for (let i = 0; i < products.length; i++) {
             if (e.target.name === products[i].name) {
                 cartItems = cartItems.filter(obj => obj.name !== products[i].name)
                 setListItems(cartItems)
                 console.log('items filtered')
             }
         }
     }

let sum
 function MakeItems() {
     if (cartItems !== []) {
         for (let i = 0; i < cartItems.length; i++) {
             let temp = []
             temp.push(cartItems[i].price * cartItemsNum[i])
             sum = temp.reduce((a, b) => a + b, 0)
         }
         return <>
         <div> Total : {sum}</div>
         {cartItems.map((indiv) =>  (
            <li className='item' key={indiv.id} name={indiv.name}>
                <img className='item-img' src={"./img/" + indiv.image[0]} alt={indiv.alt} />
                <div className='item-des'>
                    <div className='title'>{indiv.name}</div>
                    <div className='snippet'>{indiv.description}</div>
                </div>
                <div className='important'>
                    <div className='quantity'>{cartItemsNum[indiv.id]} {cartItemsNum[indiv.id] > 1 ? 'items' : 'item'}</div>
                    <div className='price'>{indiv.price}</div>
                    <div className='adding'>Adding to:</div>
                    <div className='math'>{(indiv.price) * (cartItemsNum[indiv.id])}</div>
                </div>
                <button className='remove' name={indiv.name} onClick={removeItem}>X</button>
            </li>))}
            </>
     } else {
        return (
            <div id="empty">
                <img id='emptycart' src="../img/shoppingcart.svg" />
                    There's nothing here...
            </div>

        )
     }
 }



 useEffect(() => {
{    <>
    <img id="border" src={border} alt='border image' />
    <div id="main">
        <ul id="item-list">
            <MakeItems />
            <div id="bottom">
                <button id="checkout" onClick={goToCheckout}>Checkout</button>
            </div>
        </ul>
    </div>
</>}
