import React, {useContext, useEffect, useState} from 'react'
import './styles.css'
import { cart } from '../bagcontext'
import { CreateProducts } from '../products'
import { useNavigate } from 'react-router-dom'
import { bag } from '../../App'
import border from '../img/border.png'
import { products } from '../productslist'


let ProductSearch = () => {
    const navigate = useNavigate()
    let selectedProduct = useContext(bag)
    selectedProduct.length = 0
    let cartItems = useContext(cart) 
    let [bagDisplay, setBagDisplay] = useState('none') 

    let goToBag = () => {
        navigate('/shopping-cart')
    }

    useEffect(() => {
        if (cartItems.length > 0) {
            setBagDisplay('block')
        }
    });

    let reSort = (e) => {
        if (e.target.value === "lowtohigh") {

        } else if (e.target.value === "hightolow") {

        }
    }

    let MakeSidebar = () => {
        let keywordCheck = []
        products.map((product) => (
            product.keywords.map((keyword) => {
                if (!keywordCheck.includes(keyword)) {
                    keywordCheck.push(keyword)
                    return  <div>
                                <input className='checkbox' id={keyword} type='checkbox' name={keyword} value={keyword}></input>
                                <label className='check' htmlFor={keyword}>{keyword}</label>
                            </div>
                }
            })
        ))
    }

    return  <>
                <div id="bag-counter" style={{display: bagDisplay}}>{cartItems.length}</div>
                <img id="bag-popup" src="../img/shoppingcart.svg" alt="shopping-cart"onClick={goToBag}/>
                <div id="top">
                    <img id="title-img" src={border} alt='border'/>
                    <div id="main-title" className="title"></div>
                </div>
                <div id="topics">
                    <form id="input">
                        <div>
                            <input class="checkbox" id="Instock" type="checkbox" name="Instock" value="Instock" />
                            <label class="check">In-stock</label>
                            <MakeSidebar />
                        </div>
                        <input id="parameter-btn" type="button" value="Submit" />
                    </form>
                </div>
                <div id="main">
                    <div id="body-cont">
                        <div id="sorting">
                            <select name="inputdata" id="sort" onchange={reSort}>
                                <option value="reccomended">Reccomended</option>
                                <option value="lowtohigh">Price: low to high</option>
                                <option value="hightolow">Price: high to low</option>
                            </select>
                        </div>
                        <ul id="products">
                            <CreateProducts />
                        </ul>
                    </div>
                </div>
            </>
}

export default ProductSearch
