import { BrowserRouter, Routes, Route } from "react-router-dom";
import Homepage from "./components/homepage/scripts"
import FAQ from "./components/faq/scripts"
import ProductSearch from "./components/productsearch/scripts"
import ProductInfo from "./components/productinfo/scripts"
import ShoppingCartPage from "./components/shoppingcart/scripts";

import { useState } from "react";

const RouteSwitch = () => {

  const [cart, setCart] = useState([])
  const [finalCart, setFinalCart] = useState([])

  const [cartNum, setCartNum] = useState([])
  const [finalCartNum, setFinalCartNum] = useState([])

  setCart(cart => [cart, value])

  //setTheArray(prevArray => [...prevArray, newValue])

  setFinalCart((insert) => {
    finalCart.push(insert)
  })

  setCartNum((inset) => {
    cart.push(inset)
  })

  setFinalCartNum((insert) => {
    finalCart.push(insert)
  })

  return (
      <Routes>
        <Route path="/" element={<Homepage />} />
        <Route path="/homepage" element={<Homepage />} />
        <Route path="/faq" element={<FAQ />} />
        <Route path="/search" element={<ProductSearch />} />
        <Route path="/product-info" element={<ProductInfo setCart={setCart} setCartNum={setCartNum}/>} />
        <Route path="/shopping-cart" element={<ShoppingCartPage cart={cart} setFinalCart={setFinalCart} setFinalCartNum={setFinalCartNum} cartNum={cartNum}/>} />
      </Routes>
  );
};

export default RouteSwitch;
