import React from 'react'
import ReactDOM from 'react-dom'
import products from './productslist'

function CreateProducts() {
    return  <>
    {products.map((product) => (
        <li className='product' key={product.id}>
            <img src={product.image[0]} alt={product.alt} className='images' id={product.id} onClick={getItem} />
            <div className='name'>{product.name}</div>
            <div className='price'>{product.price}</div>
        </li>
    ))}
    </>

}

let imageAmount = document.querySelectorAll('.images')
let selectedProduct = {}

function getItem() {
    selectedProduct = {}
    for (let n = 0; n < imageAmount.length; n++) {
        for (let a = 0; a < products.length; a++) {
            if (imageAmount[n].id == Object.values(products)[a].id) {
                selectedProduct[products[a]] = products[a]
                sessionStorage.setItem("selectedProduct", JSON.stringify(selectedProduct))
                //location.href = '../productinfo/main.html'
            }
        }
    }
}

export {CreateProducts}
