function reSort() {    
    if (document.getElementById('sort').value === "lowtohigh" && list.childElementCount > 1) {
        let pricesArray = []
        let sortedArray = []

        for (i = 0; i < productAmount.length; i++) { 
            pricesArray.push((productAmount[i].childNodes[2].textContent).replace('$', ''))
            pricesArray.sort(function(a, b){return a-b})
        }

        for (i = 0; i < productAmount.length; i++) { 
             for (a = 0; a < Object.keys(products).length; a++) {
                 if (pricesArray[i] == Object.values(products)[a].price && !sortedArray.includes(Object.values(products)[a])) { // and hasn't already appeared
                    sortedArray.push(Object.values(products)[a])
                 }
            }
        }

        removeProduct()

            for (i = 0; i < sortedArray.length; i++) {
                let listItem = document.createElement('li')
                let listImg = document.createElement('img')
                let listName = document.createElement('div')
                let listPrice = document.createElement('div')

                listItem.setAttribute('class', 'product')

                listImg.src = sortedArray[i].image[0]
                listImg.alt = "Picture of " + sortedArray[i].name

                listName.setAttribute('class', 'name')
                listName.textContent = sortedArray[i].name

                listPrice.setAttribute('class', 'price')
                listPrice.textContent = '$' + sortedArray[i].price

                listItem.appendChild(listImg)
                listItem.appendChild(listName)
                listItem.appendChild(listPrice)
                list.appendChild(listItem)
            }


    } else if (document.getElementById('sort').value === "hightolow" && list.childElementCount > 1) {
        let pricesArray = []
        let sortedArray = []

        for (i = 0; i < productAmount.length; i++) { 
            pricesArray.push((productAmount[i].childNodes[2].textContent).replace('$', ''))
            pricesArray.sort(function(a, b){return b-a})
        }

        for (i = 0; i < productAmount.length; i++) { 
             for (a = 0; a < Object.keys(products).length; a++) {
                 if (pricesArray[i] == Object.values(products)[a].price && !sortedArray.includes(Object.values(products)[a])) {
                    sortedArray.push(Object.values(products)[a])
                 }
            }
        }

        removeProduct()

            for (i = 0; i < sortedArray.length; i++) {
                let listItem = document.createElement('li')
                let listImg = document.createElement('img')
                let listName = document.createElement('div')
                let listPrice = document.createElement('div')

                listItem.setAttribute('class', 'product')

                listImg.src = sortedArray[i].image[0]
                listImg.alt = "Picture of " + sortedArray[i].name

                listName.setAttribute('class', 'name')
                listName.textContent = sortedArray[i].name

                listPrice.setAttribute('class', 'price')
                listPrice.textContent = '$' + sortedArray[i].price

                listItem.appendChild(listImg)
                listItem.appendChild(listName)
                listItem.appendChild(listPrice)
                list.appendChild(listItem)
            }
        }
}
