import React from 'react'
import ReactDOM from 'react-dom'
import './styles.css'
import { CreateProducts } from '../products'
import { useNavigate } from 'react-router-dom'
import { useState, useEffect } from 'react'
import products from '../productslist'

import { useContext } from 'react';
import { bag, cart, cartNum } from '../bagcontext'

function ProductInfo() {
    let selectedProduct = useContext(bag)
    let cartItems = useContext(cart) 
    let cartItemsNum = useContext(cartNum) 

    const [bagAmount, setBagAmount] = useState(0);

    const navigate = useNavigate()

    function setBagItems(e) {
        e.preventDefault()
        for(let i = 0; i < products.length; i++) {
            if (e.target.className === products[i].name) {
                cartItems.push(products[i])
                cartItemsNum.push(e.target.value)
                console.log(cartItems.length)
            }
        }
    }

    function goToBag() {
        navigate('/shopping-cart')
    }


    useEffect(() => {
            setBagAmount(cartItems.length)
            if (cartItems.length > 1) {
                console.log('passed parameter')
                document.getElementById('bag-counter').style.display = 'block'
            }
    }, [bagAmount, cartItems]);

    if (typeof(selectedProduct) == 'undefined') {
        console.log('hey')
        navigate('/search')
    }

    return  <>
                <div id="bag-counter">{bagAmount}</div>
                <img id="bag-popup" src="./img/shoppingcart.svg" onClick={goToBag}/>
                <div id="imgdes">
                    <div id="lefty">
                        <img src={'./img/' + (selectedProduct)[0][0].image[0]} alt={(selectedProduct)[0][0].alt} />
                    </div>
                    <div id="righty">
                        <div id='title'>{(selectedProduct)[0][0].name}</div>
                        <div id='itemtype'>{(selectedProduct)[0][0].snippet}</div>
                        <div id='price'>{(selectedProduct)[0][0].price}</div>
                        <form id='purchase'>
                            <input id='quantity' className={(selectedProduct)[0][0].name} type="number" name="quantity" min="1" max="99"></input>
                            <input type="submit" id='bag' className={(selectedProduct)[0][0].name} onClick={setBagItems} />
                        </form>
                        <div id='item-des'>{(selectedProduct)[0][0].description}</div>
                    </div>
                </div>
                <div id="body-cont">
                    <div id="reccs">Recommended for you:</div>
                    <ul id="products">
                        <CreateProducts />
                    </ul>
                </div>
            </>
}

export default ProductInfo
