import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import products from './productslist'
import { useNavigate } from 'react-router-dom'
import { useContext } from 'react'
import { bag } from './bagcontext'
import { selectedFilter } from '../App'


function CreateProducts() {

    const navigate = useNavigate()
    const bagitems = useContext(bag)
    let selectedFilterHere = useContext(selectedFilter)
    const [itemAmount, setitemAmount] = useState(selectedFilterHere);

    console.log(selectedFilterHere)
    function goToNewProduct(e) {
        for(let i = 0; i < products.length; i++) {
            if (e.target.alt === products[i].alt) {
                bagitems.length = 0
                bagitems.push(products[i])
                navigate('/product-info')
            }
        }
        if(document.getElementById('purchase')) {
            console.log('bug')
            document.getElementById('purchase').style.display = 'block'
        }
    }




    useEffect(() => {
        if (selectedFilterHere !== '') {
            products.filter(filterer)
            console.log(products)
        }

        function filterer(item) {
            item.keywords.includes(selectedFilterHere) 
        }
    }, [])


    return  <>
    {products.map((product) => (
        <li className='product' key={product.id}>
            <img src={'./img/' + (product.image[0])} alt={product.alt} className='images' id={product.id} onClick={goToNewProduct} />
            <div className='name'>{product.name}</div>
            <div className='price'>{product.price}</div>
        </li>
    ))}
    </>

}


export {CreateProducts}
