import {Footer} from './components/footer'
import {Header} from './components/header'

import RouteSwitch from './RouteSwitch';
import { useState, useEffect } from 'react';

function App() {
  const [cart, setCart] = useState([])
  const [finalCart, setFinalCart] = useState([])

  const [cartNum, setCartNum] = useState([])
  const [finalCartNum, setFinalCartNum] = useState([])

  setCart((inset) => {
    cart.push(inset)
    console.log(cart)
  })

  setFinalCart((insert) => {
    finalCart.push(insert)
  })

  setCartNum((inset) => {
    cart.push(inset)
    console.log(cart)
  })

  setFinalCartNum((insert) => {
    finalCart.push(insert)
  })

  return (
    <>
    <Header />
      <RouteSwitch />
    <Footer />
    </>
  );
}

export default App


