import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import products from './productslist'
import { useNavigate } from 'react-router-dom'
import { useContext } from 'react'
import { bag } from './bagcontext'


function CreateProducts() {

    const navigate = useNavigate()

    const [selectedProduct, setSelectedProduct] = useState([])
    const bagitems = useContext(bag)

    function setterSelectedProduct(e) {
        for(let i = 0; i < products.length; i++) {
            if (e.target.alt === products[i].alt) {
                setSelectedProduct(selectedProduct.push(products[i]))
                bagitems.push(selectedProduct)
                navigate('/product-info')
            }
        }
    }



    return  <>
    {products.map((product) => (
        <li className='product' key={product.id}>
            <img src={'./img/' + (product.image[0])} alt={product.alt} className='images' id={product.id} onClick={setterSelectedProduct} />
            <div className='name'>{product.name}</div>
            <div className='price'>{product.price}</div>
        </li>
    ))}
    </>

}


export {CreateProducts}
