import React from 'react'
import products from './productslist'
import { useNavigate, useParams } from 'react-router-dom'
import { useContext } from 'react'
import { bag } from '../App'
import { showCart } from './bagcontext'


let CreateProducts = (props) => {

    let letShowCart = useContext(showCart)
    const navigate = useNavigate()
    const bagitems = useContext(bag)
    const { selectedFilterHere } = useParams();

    let goToNewProduct = (e) => {
        for(let i = 0; i < products.length; i++) {
            if (e.target.alt === products[i].alt) {
                bagitems.length = 0
                bagitems.push(products[i])
                navigate('/product-info')
                letShowCart = 'block'
            }
        }
    }


    let GetProducts = () => {
        let productsFiltered = []

        function filterer(item) {
            if (item.keywords.includes(selectedFilterHere) === true) {
                productsFiltered.push(item)
            }
        }

        function propsFilterer(item) {
            for(let i = 0; i < products.length; i++) {
                let pass = true

                for (let a = 0; a < Object.keys(props.filter).length ; a++) { 

                    if (products[i].keywords.includes(Object.values(props.filter)[a])) {
                        console.log('passed')

                    } else {
                        pass = false
                        console.log('failed')

                    }

                }
                console.log(pass)
                if (pass && !productsFiltered.includes(item)) {

                    console.log("pushed")
                    productsFiltered.push(item)
                }
            }
        }

        if (props.filter) {
            products.filter(propsFilterer)
            console.log(productsFiltered)
            if (props.length) {
                if (productsFiltered.length > props.length) {
                    productsFiltered.length = props.length
                }
            }

            return  <>
            {productsFiltered.map((product) => (
                <li className='product' key={product.id}>
                    <img src={'../img/' + (product.image[0])} alt={product.alt} className='images' id={product.id} onClick={goToNewProduct} />
                    <div className='name'>{product.name}</div>
                    <div className='price'>{product.price}</div>
                </li>
                ))}
            </>



        } else if (selectedFilterHere !== undefined) {
            products.filter(filterer)
            if (props.length) {
                if (productsFiltered.length > props.length) {
                    productsFiltered.length = props.length
                }
            }
            if (props.sort) {
                let pricesArray = []
                let sortedArray = []

                for (i = 0; i < productsFiltered.length; i++) { 
                    pricesArray.push(productsFiltered[i].price)
                    pricesArray.sort(function(a, b){return a-b})
                }

                for (i = 0; i < productAmount.length; i++) { 
                     for (a = 0; a < Object.keys(products).length; a++) {
                         if (pricesArray[i] == Object.values(products)[a].price && !sortedArray.includes(Object.values(products)[a])) { // and hasn't already appeared
                            sortedArray.push(Object.values(products)[a])
                         }
                    }
                }
            }

            return  <>
            {productsFiltered.map((product) => (
                <li className='product' key={product.id}>
                    <img src={'../img/' + (product.image[0])} alt={product.alt} className='images' id={product.id} onClick={goToNewProduct} />
                    <div className='name'>{product.name}</div>
                    <div className='price'>{product.price}</div>
                </li>
                ))}
            </>

        } else {
            return  <>
            {products.map((product) => (
                <li className='product' key={product.id}>
                    <img src={'./img/' + (product.image[0])} alt={product.alt} className='images' id={product.id} onClick={goToNewProduct} />
                    <div className='name'>{product.name}</div>
                    <div className='price'>{product.price}</div>
                </li>
                ))}
            </>
        }}



    return  <>
    <GetProducts />
    </>

}


export {CreateProducts}
